import groovy.json.JsonSlurper
def response = context.testCase.getTestStepByName("login").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(response)
def token = holder.accessToken
context.testCase.TestSuite.setPropertyValue("token","bearer "+token)


//can i store it in the test case
context.testCase.setPropertyValue("token","bearer "+token)
//i want to access this acroos project level
context.testCase.testSuite.project.setPropertyValue("token","bearer "+token)

import groovy.json.JsonSlurper
def response = context.testCase.getTestStepByName("AllUser").getPropertyValue("response")
def holder = new JsonSlurper().parseText(response)
def count = holder.count.toInteger()
for(def i=0;i<count;i++)
{
	if(holder.users[i].email=="george129@gmail.com")
	{
		def id = holder.users[i]._id
		log.info id
		def id1 = id.toString()
		context.testCase.testSuite.setPropertyValue("id",id1)
		context.testCase.setPropertyValue("id",id1)
//i want to access this acroos project level
		context.testCase.testSuite.project.setPropertyValue("id",id1)
	}
}

